table {
        width: 100%;
        border-collapse: collapse;
    }
    th, td {
        border: 1px solid black;
        padding: 8px;
        text-align: center;
    }
    th {
        background-color: #f2f2f2;
    }
    /* Establecer el ancho de las columnas */
    th.item, td.item {
        width: 10%;
    }
    th.sintomas, td.sintomas {
        width: 30%;
    }
    th.sesion, td.sesion {
        width: 5%;
    }
